import React from 'react';
import logo from '../../logo.svg';
import './Image.css';
import UpdateForm from '../UpdateForm/UpdateForm';


class Image extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            update : undefined
        }
    }

    deleteByID (key) {
        const check = window.confirm("Confirm delete Meme having id "+ key);
        if (check){    
        fetch('https://xmeme-backend-ashutoshv.herokuapp.com/memes/'+key, {
                method: "delete"
            })
            .catch(err => {console.log(err);})
         this.setState({
             update : ""
         });

        } 
    }

    editByID (key)  {
        return(
            <UpdateForm />
        );
    }
render() {
    const {key} = this.props;
    return (
            <div className="meme-card">
                <div className="meme-posted">
                    <div className="header">
                        <div className="header1"><h3>{this.props.name} {this.props.id}</h3></div>
                        <div className="header2">
                            <button onClick={() => this.editByID(this.props.id)}>Edit</button>
                            <button onClick={()=> this.deleteByID(this.props.id)}>Delete</button>
                        </div>
                    </div>
                    <p>{this.props.caption}</p>
                    <img width="80%" src={this.props.url} alt="Image not available"/>
                </div>
            </div>
      );
    }
}

export default Image;
