import logo from './logo.svg';
import './App.css';
import Form from './components/Form/Form';
import Image from './components/Image/Image';
import { Component } from 'react';
// import React from 'react';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      memes: []
    };
  }

  getMemes = () => {
    // const api_call = await fetch('http://localhost:2000/memes') 
    fetch('http://localhost:2000/memes')
      .then(response => response.json())
      // .then(data=> console.log(data));
      .then((result)=> {
        this.setState({
          memes: result.memes
        });
      },
      (error) => {
        this.setState({
          error
        });
      } 
      )
  }

  render() {
    return (
      <div className="">
          <Form />
          <Image memes={this.state.memes}/>
          <div>
          { this.getMemes()}
          </div>

      </div> 
    );
  }
}


export default App;
